    private void equalActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_equalActionPerformed
        // TODO add your handling code here:
        if(choice!=6)
        b = Float.parseFloat(s);
        
        s="";
        switch(choice)
        {
            case 1: 
                scr.setText(Float.toString(a+b)); 
                  subscr.setText(Float.toString(a+b));
                    break;
            case 2:
                scr.setText(Float.toString(a-b));
                subscr.setText(Float.toString(a-b));
                    break;
            case 3: if(b!=0)
            
            {scr.setText(Float.toString(a/b));
            subscr.setText(Float.toString(a/b));
            }
            else if(a==0&&b==0)
            {scr.setText("NaN");
            subscr.setText("");
            }
           
        
             
                else
            {scr.setText("âˆž");
            subscr.setText("");
            }
                    break;
            case 4:
                   if(a==0&&b==0)
                   {scr.setText("NaN");
                   subscr.setText("");
                   }
                   else if(b==0)
                   {scr.setText("NaN");
                   subscr.setText("");
                   }
                   else
                  
                   {scr.setText(Float.toString((int)a%(int)b));
                   subscr.setText(Float.toString((int)a%(int)b));
                   }
                    break;
            case 5:scr.setText(Float.toString(a*b));
                    subscr.setText(Float.toString(a*b));
                    break;
                  
        }
        flag = 0;
        s="";
      
        
    }//GEN-LAST:event_equalActionPerformed

